// Payment.jsx
import React, { useState, useEffect, useRef } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { toast } from "react-toastify";
import { createPayment, resetPayment, checkPaymentStatus, cancelPaymentPolling } from "../../features/payment/paymentSlice";
import { motion } from "framer-motion";
import {
  Box, Container, Typography, Radio, RadioGroup, FormControlLabel,
  FormControl, Button, Paper, Divider, CircularProgress, Grid
} from "@mui/material";
import PaymentIcon from "@mui/icons-material/Payment";
import LocalAtmIcon from "@mui/icons-material/LocalAtm";
import HomeIcon from "@mui/icons-material/Home";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";

// ‚ú® CUSTOM HOOK: T√°ch logic polling ra kh·ªèi component ƒë·ªÉ t√°i s·ª≠ d·ª•ng v√† qu·∫£n l√Ω d·ªÖ d√†ng h∆°n
const usePaymentPolling = (orderId, shouldPoll, onPaymentSuccess, onPaymentFail, onTimeout) => {
  const dispatch = useDispatch();
  const pollIntervalRef = useRef(null);
  const pollCountRef = useRef(0);

  const MAX_POLLING_RETRIES = 12; // 12 l·∫ßn * 5s = 60s
  const POLLING_INTERVAL = 5000; // 5 gi√¢y

  const stopPolling = () => {
    if (pollIntervalRef.current) {
      clearInterval(pollIntervalRef.current);
      pollIntervalRef.current = null;
      console.log('Polling stopped.');
      dispatch(cancelPaymentPolling());
    }
  };

  const startPolling = () => {
    // NgƒÉn ch·∫∑n vi·ªác ch·∫°y nhi·ªÅu polling c√πng l√∫c
    stopPolling();
    pollCountRef.current = 0;
    console.log('Starting payment status polling...');

    const checkStatus = async () => {
      // D·ª´ng n·∫øu qu√° s·ªë l·∫ßn th·ª≠
      if (pollCountRef.current >= MAX_POLLING_RETRIES) {
        console.log('Max polling retries reached. Stopping.');
        stopPolling();
        onTimeout?.(); // G·ªçi h√†m khi timeout
        return;
      }

      pollCountRef.current += 1;
      console.log(`Checking payment status for order: ${orderId} (Attempt ${pollCountRef.current}/${MAX_POLLING_RETRIES})`);

      try {
        const resultAction = await dispatch(checkPaymentStatus(orderId));
        if (checkPaymentStatus.fulfilled.match(resultAction)) {
          const paymentStatus = resultAction.payload?.payment?.status;
          console.log('Payment status check result:', paymentStatus);

          if (paymentStatus === 'paid') {
            stopPolling();
            onPaymentSuccess(); // G·ªçi h√†m khi th√†nh c√¥ng
          } else if (paymentStatus === 'failed') {
            stopPolling();
            onPaymentFail(); // G·ªçi h√†m khi th·∫•t b·∫°i
          }
        }
      } catch (err) {
        console.error("Error checking payment status:", err);
      }
    };

    // Ki·ªÉm tra ngay l·∫ßn ƒë·∫ßu ti√™n, sau ƒë√≥ l·∫∑p l·∫°i
    checkStatus();
    pollIntervalRef.current = setInterval(checkStatus, POLLING_INTERVAL);
  };

  useEffect(() => {
    if (shouldPoll && orderId) {
      startPolling();
    } else {
      stopPolling();
    }

    // H√†m d·ªçn d·∫πp: t·ª± ƒë·ªông d·ª´ng polling khi component unmount
    return () => stopPolling();
  }, [shouldPoll, orderId]);

  return { stopPolling };
};


const Payment = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const dispatch = useDispatch();

  // L·∫•y d·ªØ li·ªáu t·ª´ location.state m·ªôt c√°ch an to√†n
  const { orderId, totalPrice = 0, preferredMethod = 'PayOS', directPayment = false } = location.state || {};

  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState(preferredMethod);
  const [showPayosIframe, setShowPayosIframe] = useState(false);

  const { payment, loading, error, success } = useSelector((state) => state.payment);

  // üí° S·ª¨ D·ª§NG CUSTOM HOOK
  // Logic polling gi·ªù ƒë√¢y ch·ªâ c·∫ßn khai b√°o, kh√¥ng c·∫ßn vi·∫øt l·∫°i trong component
  const handlePaymentSuccess = () => {
    toast.success("Thanh to√°n th√†nh c√¥ng!");
    dispatch(resetPayment());
    sessionStorage.setItem(`payment_${orderId}_completed`, 'true'); // ƒê√°nh d·∫•u ƒë√£ xong
    navigate('/', { replace: true });
  };

  const handlePaymentFail = () => {
    toast.error("Thanh to√°n th·∫•t b·∫°i!");
    dispatch(resetPayment());
    navigate('/payment-result', { state: { status: 'failed', orderId }, replace: true });
  };

  const handlePollingTimeout = () => {
      toast.warn("Kh√¥ng nh·∫≠n ƒë∆∞·ª£c ph·∫£n h·ªìi thanh to√°n. Vui l√≤ng ki·ªÉm tra l·ªãch s·ª≠ ƒë∆°n h√†ng.");
      navigate('/order-history', { replace: true });
  };

  const { stopPolling } = usePaymentPolling(
    orderId,
    showPayosIframe, // Ch·ªâ poll khi iframe ƒë∆∞·ª£c hi·ªÉn th·ªã
    handlePaymentSuccess,
    handlePaymentFail,
    handlePollingTimeout
  );

  // T·ª± ƒë·ªông x·ª≠ l√Ω thanh to√°n n·∫øu ƒë·∫øn t·ª´ trang l·ªãch s·ª≠ ƒë∆°n h√†ng
  useEffect(() => {
    if (orderId && directPayment && selectedPaymentMethod === 'PayOS') {
      handlePayment();
    }
  }, [orderId, directPayment]);

  // X·ª≠ l√Ω khi API createPayment tr·∫£ v·ªÅ k·∫øt qu·∫£
  useEffect(() => {
    if (error) {
      toast.error(error);
      dispatch(resetPayment());
    }

    if (success) {
      if (selectedPaymentMethod === 'PayOS') {
        if (payment?.paymentUrl) {
          setShowPayosIframe(true);
          toast.info("C·ªïng thanh to√°n PayOS ƒë√£ s·∫µn s√†ng.");
        } else {
          toast.error("Kh√¥ng th·ªÉ t·∫°o li√™n k·∫øt thanh to√°n PayOS.");
          dispatch(resetPayment());
        }
