        toast.success("ƒê√£ t·∫°o ƒë∆°n h√†ng COD th√†nh c√¥ng.");
        setTimeout(() => {
          navigate('/payment-result', { state: { status: 'paid', orderId }, replace: true });
        }, 1500);
      }
    }
  }, [success, error, payment, dispatch, navigate]);

  if (!orderId) {
    toast.error("Kh√¥ng t√¨m th·∫•y th√¥ng tin ƒë∆°n h√†ng.");
    useEffect(() => navigate("/", { replace: true }), []);
    return null;
  }

  const handlePayment = () => {
    dispatch(createPayment({
      orderId: String(orderId),
      method: selectedPaymentMethod,
      replaceExisting: directPayment
    }));
  };

  const handleBackToHome = () => {
    setShowPayosIframe(false);
    stopPolling(); // D·ª´ng polling khi ng∆∞·ªùi d√πng ch·ªß ƒë·ªông tho√°t
    dispatch(resetPayment());
    navigate("/");
  };

  // C√°c h√†m render v√† JSX kh√¥ng thay ƒë·ªïi nhi·ªÅu
  const renderPaymentMethods = () => {
      // ... (Gi·ªØ nguy√™n code render c·ªßa b·∫°n)
  };

  return (
    <Container maxWidth="lg" sx={{ py: 6 }}>
        {/* ... (Gi·ªØ nguy√™n ph·∫ßn JSX giao di·ªán c·ªßa b·∫°n) */}
        {/* Ch·ªâ c·∫ßn ƒë·∫£m b·∫£o c√°c h√†m onClick g·ªçi ƒë√∫ng h√†m ƒë√£ ƒë·ªãnh nghƒ©a ·ªü tr√™n */}
    </Container>
  );
};

